mapper.select:
  SELECT
 id, name, remark,
 create_time, create_user, update_time,
 update_user, status, yn,
  version
  FROM 
  WHERE yn=0

  AND  id = #{id}


  AND name LIKE #{name}


  AND create_time > #{createTime}


  AND create_time < #{endTime}

ORDER BY create_time DESC
limit #{startRow},#{pageSize}

SELECT
count(1)
FROM 
WHERE yn=0

  AND  id = #{id}


  AND name LIKE #{name}


  AND create_time > #{createTime}


  AND create_time < #{endTime}

mapper.update:
UPDATE  SET name=#{name},remark=#{remark}, update_time=now(),update_user=#{updateUser},
remark=#{remark},status=#{status},version=version+1
WHERE yn=0   AND  id = #{id}
